package com.example.tugas1;

import androidx.appcompat.app.AppCompatActivity;

import android.os.Bundle;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import java.util.ArrayList;
import java.util.List;

public class MainActivity extends AppCompatActivity {
    private RecyclerView recyclerView;
    private StudentAdapter adapter;
    private List<Student> studentList;


    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        recyclerView = findViewById(R.id.recyclerView);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));

        int spacingInPixels = getResources().getDimensionPixelSize(R.dimen.item_spacing);
        recyclerView.addItemDecoration(new SpaceItemDecoration(spacingInPixels));


        studentList = new ArrayList<>();
        // Tambahkan data mahasiswa ke list
        studentList.add(new Student("Fatahillah Abid", "A12.2020.06454", "data:image/jpeg;base64,"));
        studentList.add(new Student("Eridani Wahyu", "A12.2020.06479", "https://pm1.aminoapps.com/6876/265f509ca1648a099fc523a6458dfcb663ebce02r1-340-340v2_128.jpg"));
        studentList.add(new Student("M. Kahlil Gibran", "A12.2020.06484", "data:image/jpeg;base64,"));


        adapter = new StudentAdapter(this, studentList);
        recyclerView.setAdapter(adapter);
    }
}